public class EmployeeService\n{\n    public EmployeeWithAddress GetEmployeeWithAddress(int id)\n    {\n        var employee = _repository.GetEmployee(id);\n        return new EmployeeWithAddress { Address = employee.Address };\n    }\n\n    public EmployeeWithPhoneNumbers GetEmployeeWithPhoneNumbers(int id)\n    {\n        var employee = _repository.GetEmployee(id);\n        return new EmployeeWithPhoneNumbers { PhoneNumbers = employee.PhoneNumbers };\n    }\n}