using System;\nusing System.IO;\nusing System.Web.Services.Protocols;\nusing ReportingService2005;\n\nclass ReportGenerator\n{\n    static void Main(string[] args)\n    {\n        // Set up the reporting service URL and credentials\n        string reportServerUrl = "http://your-report-server/ReportServer/ReportService2005.asmx";\n        string username = "your-username";\n        string password = "your-password";\n        string domain = "your-domain";\n\n        // Create a new ReportingService2005 instance\n        ReportingService2005 rs = new ReportingService2005();\n        rs.Url = reportServerUrl;\n        rs.Credentials = new NetworkCredential(username, password, domain);\n\n        // Set up the report parameters\n        string reportPath = "/YourReportPath/YourReport";\n        string reportFormat = "PDF";\n        string filePath = @"C:\Path\To\Save\Report.pdf";\n\n        // Generate the report\n        byte[] reportBytes = rs.Render(reportPath, reportFormat, null, null, null, null, null);\n\n        // Save the report to disk\n        using (FileStream fs = new FileStream(filePath, FileMode.Create))\n        {\n            fs.Write(reportBytes, 0, reportBytes.Length);\n        }\n    }\n}