using System;\nusing System.Security.Cryptography;\n\npublic class LicenseFileEncryptor\n{\n    public static void EncryptFile(string filePath, string publicKey)\n    {\n        using (var rsa = RSA.Create())\n        {\n            rsa.ImportSubjectPublicKeyInfo(Convert.FromBase64String(publicKey), out _);\n            using (var fileStream = File.OpenRead(filePath))\n            {\n                using (var encryptedStream = File.Create(filePath + ".enc"))\n                {\n                    rsa.Encrypt(fileStream, encryptedStream);\n                }\n            }\n        }\n    }\n\n    public static void DecryptFile(string filePath, string privateKey)\n    {\n        using (var rsa = RSA.Create())\n        {\n            rsa.ImportPkcs8PrivateKey(Convert.FromBase64String(privateKey), out _);\n            using (var fileStream = File.OpenRead(filePath + ".enc"))\n            {\n                using (var decryptedStream = File.Create(filePath))\n                {\n                    rsa.Decrypt(fileStream, decryptedStream);\n                }\n            }\n        }\n    }\n}