using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class Program\n{\n    public static void Main()\n    {\n        float[] array = { 1.0f, 2.0f, 3.0f, 4.0f, 5.0f, 6.0f, 7.0f, 8.0f, 9.0f };\n\n        var topValues = GetTopValues(array, 5);\n\n        foreach (var tuple in topValues)\n        {\n            Console.WriteLine($"Value: {tuple.Item1}, Position: {tuple.Item2}");\n        }\n    }\n\n    public static List<(float, int)> GetTopValues(float[] array, int count)\n    {\n        var list = new List<(float, int)>();\n\n        for (int i = 0; i < array.Length; i++)\n        {\n            list.Add((array[i], i));\n        }\n\n        list.Sort((a, b) => b.Item1.CompareTo(a.Item1));\n\n        return list.Take(count).ToList();\n    }\n}