using System.Diagnostics;\n\nclass PerformanceMonitor\n{\n    private static readonly string[] _counters = new[]\n    {\n        "ASP.NET\Requests Current",\n        "ASP.NET\Requests Queued",\n        "ASP.NET\Request Wait Time",\n        "ASP.NET\Requests Per Second",\n        "ASP.NET\Bytes Total Per Second",\n        ".NET CLR Memory\% Time in GC",\n        "ASP.NET\Working Set",\n        "ASP.NET\Errors Total",\n        "ASP.NET\Exceptions Thrown Per Second",\n        "ASP.NET\Application Restarts"\n    };\n\n    public static void MonitorPerformance()\n    {\n        foreach (var counter in _counters)\n        {\n            var performanceCounter = new PerformanceCounter(counter);\n            Console.WriteLine($"{counter}: {performanceCounter.RawValue}");\n        }\n    }\n}