using Microsoft.WindowsAzure.Storage;\nusing Microsoft.WindowsAzure.Storage.Blob;\n\n// Create a CloudBlobClient instance\nCloudBlobClient blobClient = new CloudBlobClient("https://youraccount.blob.core.windows.net", \n    new StorageCredentials("youraccount", "yourkey"));\n\n// Create a CloudBlobContainer instance\nCloudBlobContainer container = blobClient.GetContainerReference("yourcontainer");\n\n// Create a CloudBlockBlob instance\nCloudBlockBlob blob = container.GetBlockBlobReference("yourblob");\n\n// Upload a file to the blob\nusing (var fileStream = new FileStream("yourfile.txt", FileMode.Open))\n{\n    blob.UploadFromStream(fileStream);\n}