using System.Text.RegularExpressions;\n\n// Your XML string\nstring xmlString = "...";\n\n// Regex pattern to match ID attributes\nstring pattern = @"ID=""(\d+)""";\n\n// Replacement function to update ID values\nMatchEvaluator replaceId = match =>\n{\n    int idValue = int.Parse(match.Groups[1].Value);\n    // Your logic to update the ID value goes here\n    idValue += 1000; // Example update logic\n    return $"ID=\"{idValue}\"";\n};\n\n// Update ID values in the XML string\nstring updatedXmlString = Regex.Replace(xmlString, pattern, replaceId);