public static string[] FormatInts(int[] ints)\n{\n    if (ints == null)\n    {\n        throw new ArgumentNullException(nameof(ints));\n    }\n\n    if (ints.Length == 0)\n    {\n        return new string[] { "" };\n    }\n\n    if (ints.Length == 1)\n    {\n        return new string[] { ints[0].ToString() };\n    }\n\n    Array.Sort(ints);\n    var ranges = new List<string>();\n    int start = ints[0];\n    int end = ints[0];\n\n    for (int i = 1; i < ints.Length; i++)\n    {\n        if (ints[i] == end + 1)\n        {\n            end = ints[i];\n        }\n        else\n        {\n            ranges.Add(FormatRange(start, end));\n            start = ints[i];\n            end = ints[i];\n        }\n    }\n\n    ranges.Add(FormatRange(start, end));\n    return ranges.ToArray();\n}\n\nprivate static string FormatRange(int start, int end)\n{\n    if (start == end)\n    {\n        return start.ToString();\n    }\n    else\n    {\n        return $"{start}-{end}";\n    }\n}