using System;\nusing System.IO;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\npublic class TiffToPdfConverter\n{\n    public void ConvertTiffToPdf(string tiffFile, string pdfFile)\n    {\n        using (var tiffStream = new FileStream(tiffFile, FileMode.Open, FileAccess.Read))\n        {\n            using (var pdfStream = new FileStream(pdfFile, FileMode.Create, FileAccess.Write))\n            {\n                var document = new Document();\n                var writer = PdfWriter.GetInstance(document, pdfStream);\n                document.Open();\n\n                var tiffReader = new TiffImage.GetTiffImage(tiffStream);\n                for (int i = 0; i < tiffReader.NumberOfPages; i++)\n                {\n                    var image = TiffImage.GetTiffImage(tiffStream, i + 1);\n                    document.Add(image);\n                }\n\n                document.Close();\n            }\n        }\n    }\n}