public static List<string> GetImageList()\n{\n    List<string> imageList = new List<string>();\n    Assembly assembly = Assembly.LoadFrom("YourDllName.dll");\n    string[] resources = assembly.GetResources();\n    foreach (string resource in resources)\n    {\n        if (resource.EndsWith(".png") || resource.EndsWith(".jpg") || resource.EndsWith(".bmp"))\n        {\n            imageList.Add(resource);\n        }\n    }\n    return imageList;\n}