using Microsoft.Win32;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string frameworkVersion = "v4.0.30319"; // Replace with your desired framework version\n        string registryKey = $"HKEY_LOCAL_MACHINE\\SOFTWARE\\Microsoft\\.NETFramework\\{frameworkVersion}\\csc.exe";\n\n        using (RegistryKey key = Registry.LocalMachine.OpenSubKey(registryKey))\n        {\n            if (key != null)\n            {\n                string cscPath = (string)key.GetValue(null);\n                Console.WriteLine($"csc.exe located at: {cscPath}");\n            }\n            else\n            {\n                Console.WriteLine("csc.exe not found in registry.");\n            }\n        }\n    }\n}