using System;\n\nclass Program\n{\n    static void Main()\n    {\n        // Set the console title\n        Console.Title = "Advanced Console IO Demo";\n\n        // Save the current cursor position\n        int initialCursorX = Console.CursorLeft;\n        int initialCursorY = Console.CursorTop;\n\n        // Move the cursor to an arbitrary location and write text\n        Console.SetCursorPosition(10, 5);\n        Console.Write("Text at position (10, 5)");\n\n        // Change foreground and background colors\n        Console.ForegroundColor = ConsoleColor.White;\n        Console.BackgroundColor = ConsoleColor.Blue;\n\n        // Write colored text at another location\n        Console.SetCursorPosition(20, 10);\n        Console.Write("Colored Text at position (20, 10)");\n\n        // Reset colors\n        Console.ResetColor();\n\n        // Restore the initial cursor position\n        Console.SetCursorPosition(initialCursorX, initialCursorY);\n\n        Console.ReadKey(); // Wait for a key press before closing\n    }\n}