protected void GridView1_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        DataRow row = ((DataRowView)e.Row.DataItem).Row;\n        string originalValue = row["ColumnName"].ToString();\n        string newValue = TransformValue(originalValue); // Apply transformation logic here\n        e.Row.Cells[0].Text = newValue; // Update the cell text with the new value\n    }\n}\n\nstring TransformValue(string originalValue)\n{\n    // Implement your transformation logic here\n    // For example:\n    if (originalValue == "True")\n        return "Yes";\n    else\n        return "No";\n}