using Gtk;\n\nclass MyWindow : Window\n{\n    private TextView textView;\n    private Menu contextMenu;\n\n    public MyWindow() : base("ContextMenu Example")\n    {\n        textView = new TextView();\n        textView.ButtonPressEvent += OnTextViewButtonPressEvent;\n\n        contextMenu = new Menu();\n        var item = new MenuItem("Example Item");\n        item.Activated += OnItemActivated;\n        contextMenu.Append(item);\n        contextMenu.ShowAll();\n\n        Add(textView);\n    }\n\n    private void OnTextViewButtonPressEvent(object o, ButtonPressEventArgs args)\n    {\n        if (args.Event.Button == 3) // Right mouse button\n        {\n            contextMenu.Popup();\n        }\n    }\n\n    private void OnItemActivated(object sender, EventArgs e)\n    {\n        Console.WriteLine("Context menu item activated");\n    }\n}