using RazorEngine;\nusing RazorEngine.Templating;\n\n// Define your template\nstring template = @"\n<html>\n    <body>\n        <h1>Hello @Model.Name!</h1>\n    </body>\n</html>";\n\n// Define your model\npublic class EmailModel\n{\n    public string Name { get; set; }\n}\n\n// Render the template\nEmailModel model = new EmailModel { Name = "John Doe" };\nstring html = Engine.Razor.RunCompile(template, "templateKey", typeof(EmailModel), model);\n\n// Use the rendered HTML as the email body