try\n{\n    // ...\n}\ncatch (Exception ex)\n{\n    LogException(ex);\n    throw;\n}\n\nprivate void LogException(Exception ex)\n{\n    // Log the exception details, including the assembly version\n    string assemblyVersion = typeof(MyExceptionHelperClass).Assembly.GetName().Version.ToString();\n    Log.WriteLine($"Exception caught: {ex.Message} (Assembly version: {assemblyVersion})");\n}