using System;\nusing System.Reflection;\nusing System.Security.Policy;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        foreach (Object ev in Assembly.GetExecutingAssembly().Evidence)\n        {\n            if (ev is Zone)\n            {\n                Zone zone = (Zone)ev;\n                Console.WriteLine("Security zone: " + zone.SecurityZone);\n                break;\n            }\n        }\n    }\n}