using Microsoft.SharePoint.Client;\n\n// Create a SharePoint list\nList list = ctx.Web.Lists.Add(new ListCreationInformation\n{\n    Title = "Transactional Data",\n    TemplateType = (int)ListTemplateType.GenericList\n});\n\n// Add columns\nlist.Fields.AddFieldAsXml("<Field Name='Id' Type='Integer' />", true, AddFieldOptions.DefaultValue);\nlist.Fields.AddFieldAsXml("<Field Name='Date' Type='DateTime' />", true, AddFieldOptions.DefaultValue);\nlist.Fields.AddFieldAsXml("<Field Name='OrderId' Type='Lookup' />", true, AddFieldOptions.DefaultValue);\nlist.Fields.AddFieldAsXml("<Field Name='Quantity' Type='Integer' />", true, AddFieldOptions.DefaultValue);\nlist.Fields.AddFieldAsXml("<Field Name='ItemName' Type='Text' />", true, AddFieldOptions.DefaultValue);\nlist.Fields.AddFieldAsXml("<Field Name='Title' Type='Text' />", true, AddFieldOptions.DefaultValue);\n\n// Query the list\nCamlQuery query = new CamlQuery\n{\n    ViewXml = "<View><Query><Where><Eq><FieldRef Name='Id'/><Value Type='Integer'>1</Value></Eq></Where></Query></View>"\n};\nListItemCollection items = list.GetItems(query);\nctx.Load(items);\nawait ctx.ExecuteQueryAsync();