[SecurityPermission(SecurityAction.Demand, Flags = SecurityPermissionFlag.ControlPrincipal)]\npublic void DoSomethingSecure()\n{\n    // Code that requires the ControlPrincipal permission\n}\n\n[SecurityPermission(SecurityAction.Assert, Flags = SecurityPermissionFlag.ControlPrincipal)]\npublic void DoSomethingAsserted()\n{\n    // Code that requires the ControlPrincipal permission, regardless of callers\n}\n\n[SecurityPermission(SecurityAction.PermitOnly, Flags = SecurityPermissionFlag.ControlPrincipal)]\npublic void DoSomethingRestricted()\n{\n    // Code that can only be executed if ControlPrincipal is the only permission granted\n}\n\npublic class BaseClass\n{\n    [SecurityPermission(SecurityAction.InheritanceDemand, Flags = SecurityPermissionFlag.ControlPrincipal)]\n    public virtual void DoSomethingInherited() { }\n}\n\npublic class DerivedClass : BaseClass\n{\n    // Must have the ControlPrincipal permission to inherit from BaseClass\n    public override void DoSomethingInherited() { }\n}