using System;\nusing System.Net;\nusing System.Xml;\n\nnamespace WebDAVExample\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string exchangeServer = "https://your-exchange-server.com/exchange";\n            string username = "your-username";\n            string password = "your-password";\n            string calendarPath = "/calendar";\n\n            HttpWebRequest request = (HttpWebRequest)WebRequest.Create(exchangeServer + calendarPath);\n            request.Method = "PROPFIND";\n            request.Credentials = new NetworkCredential(username, password);\n\n            HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n            XmlDocument xmlDoc = new XmlDocument();\n            xmlDoc.Load(response.GetResponseStream());\n\n            XmlNodeList appointments = xmlDoc.SelectNodes("//d:response/d:propstat/d:prop/d:resourcetype");\n            foreach (XmlNode appointment in appointments)\n            {\n                Console.WriteLine(appointment.InnerText);\n            }\n        }\n    }\n}