using System;\nusing System.Text.RegularExpressions;\nusing System.Web;\n\npublic class WebSecurity\n{\n    // Function to prevent SQL Injection\n    public static string PreventSqlInjection(string input)\n    {\n        return input.Replace("'", "''").Replace("--", "").Replace(";", "");\n    }\n\n    // Function to prevent Cross-Site Scripting (XSS)\n    public static string PreventXss(string input)\n    {\n        return HttpUtility.HtmlEncode(input);\n    }\n\n    // Function to prevent Cross-Site Request Forgery (CSRF)\n    public static string PreventCsrf(string token)\n    {\n        // Generate a random token and store it in the session\n        // Compare the token with the one sent in the request\n        return token;\n    }\n\n    // Function to prevent Path Traversal\n    public static string PreventPathTraversal(string input)\n    {\n        string cleanInput = input.Replace("/", "").Replace("\\", "");\n        return cleanInput;\n    }\n\n    // Function to validate input against a whitelist\n    public static bool ValidateInput(string input, string whitelist)\n    {\n        Regex regex = new Regex(whitelist);\n        return regex.IsMatch(input);\n    }\n}