using Spectre.Console;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        AnsiConsole.WriteLine("Select a file:");\n        var filePath = AnsiConsole.Prompt(\n            new SelectionPrompt<string>()\n                .PageSize(10)\n                .MoreChoicesText("[grey]Move up or down to reveal more options[/]")\n                .AddChoices(Directory.GetFiles(Directory.GetCurrentDirectory())));\n\n        AnsiConsole.WriteLine($"You selected: {filePath}");\n    }\n}