using System.IO;\nusing System.Security.AccessControl;\n\n// Get the App_Data folder path\nstring appDataPath = Server.MapPath("~/App_Data");\n\n// Set the permissions\nDirectoryInfo appDataDir = new DirectoryInfo(appDataPath);\nDirectorySecurity dirSecurity = appDataDir.GetAccessControl();\ndirSecurity.AddAccessRule(new FileSystemAccessRule("Network Service", FileSystemRights.Write, AccessControlType.Allow));\nappDataDir.SetAccessControl(dirSecurity);