[HttpPost]\npublic ActionResult MyAction(string button)\n{\n    switch (button)\n    {\n        case "button1":\n            // redirect to controller 1\n            break;\n        case "button2":\n            // redirect to controller 2\n            break;\n        case "button3":\n            // redirect to controller 3\n            break;\n        default:\n            // handle default case\n            break;\n    }\n}