using System;\nusing System.Runtime.InteropServices;\n\n// Import the COM+ component's interface\n[ComImport]\n[InterfaceType(ComInterfaceType.InterfaceIsIUnknown)]\n[Guid("your_component_guid")]\npublic interface IMyComponent\n{\n    void MyMethod();\n}\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Create an instance of the COM+ component\n        IMyComponent component = (IMyComponent)Marshal.BindToMoniker("clsid:your_component_progid");\n\n        // Call the method\n        component.MyMethod();\n    }\n}