using System.Web.Caching;\nusing System.Data.SqlClient;\n\n// Create a SqlCacheDependency object\nSqlCacheDependency dependency = new SqlCacheDependency();\n\n// Set the connection string and database name\nstring connectionString = "your_connection_string";\nstring databaseName = "your_database_name";\n\n// Set the table and row-specific query\nstring tableName = "your_table_name";\nstring query = $"SELECT * FROM {tableName} WHERE id = @id";\n\n// Create a SqlCommand object\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    using (SqlCommand command = new SqlCommand(query, connection))\n    {\n        command.Parameters.AddWithValue("@id", your_row_id);\n\n        // Set the CommandNotification option\n        command.Notification = null;\n        command.NotificationAutoEnlist = true;\n        command.Notification firesOn = CommandNotificationFiresOn.RowUpdate;\n\n        // Execute the command and get the notification request\n        connection.Open();\n        var notificationRequest = command.ExecuteNonQuery();\n\n        // Create the cache dependency\n        dependency = new SqlCacheDependency(notificationRequest);\n    }\n}\n\n// Insert the item into the cache with the dependency\nCache.Insert("your_cache_key", your_object, dependency);