using System;\nusing System.Web;\n\npublic partial class RedirectPage : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        string domain = Request.Url.Host;\n\n        if (domain == "domain1.com")\n        {\n            Response.Redirect("http://long-url-for-domain1.com");\n        }\n        else if (domain == "domain2.com")\n        {\n            Response.Redirect("http://long-url-for-domain2.com");\n        }\n        else\n        {\n            // Handle unknown domain or throw an error\n        }\n    }\n}