// LoadUserControl.aspx.cs\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (Request.QueryString["control"] != null)\n    {\n        string controlName = Request.QueryString["control"];\n        UserControl control = (UserControl)Page.LoadControl(controlName);\n        control.ID = controlName;\n\n        // Render the control and register the script\n        string script = "Sys.Application.add_init(function() { " +\n            "$get('" + control.ClientID + "').innerHTML = '" + control.RenderControl(new HtmlTextWriter(new StringWriter())).ToString() + "'; " +\n            "});";\n        ScriptManager.RegisterStartupScript(this, GetType(), controlName, script, true);\n    }\n}