using System;\nusing System.Management;\n\nclass ComPlusRestart\n{\n    static void Main(string[] args)\n    {\n        string serverName = "yourRemoteServerName";\n        string appName = "yourComPlusAppName";\n\n        // Create a new ManagementScope object\n        ManagementScope scope = new ManagementScope(@"\\" + serverName + @"\root\cimv2");\n\n        // Create a new ManagementPath object\n        ManagementPath path = new ManagementPath("Win32_COMApplication");\n\n        // Create a new ManagementObjectSearcher object\n        ManagementObjectSearcher searcher = new ManagementObjectSearcher(scope, path);\n\n        // Iterate through the search results\n        foreach (ManagementObject obj in searcher.Get())\n        {\n            // Check if the current object is the COM+ application we want to restart\n            if (obj["Name"].ToString() == appName)\n            {\n                // Invoke the "Restart" method\n                obj.InvokeMethod("Restart", null);\n                break;\n            }\n        }\n    }\n}