public string Encrypt(byte[] PlainTextBytes, byte[] KeyBytes, string InitialVector)\n{\n    byte[] InitialVectorBytes = Encoding.ASCII.GetBytes(InitialVector);\n    RijndaelManaged SymmetricKey = new RijndaelManaged();\n    SymmetricKey.Mode = CipherMode.CBC; // Changed to CBC mode\n    SymmetricKey.Padding = PaddingMode.PKCS7; // Using PKCS7 padding instead of PKCS5\n    ICryptoTransform Encryptor = SymmetricKey.CreateEncryptor(KeyBytes, InitialVectorBytes);\n    MemoryStream MemStream = new MemoryStream();\n    CryptoStream CryptoStream = new CryptoStream(MemStream, Encryptor, CryptoStreamMode.Write);\n    CryptoStream.Write(PlainTextBytes, 0, PlainTextBytes.Length);\n    CryptoStream.FlushFinalBlock();\n    byte[] CipherTextBytes = MemStream.ToArray();\n    MemStream.Close();\n    CryptoStream.Close();\n    return ByteToHexConversion(CipherTextBytes);\n}