using Saxon.Api;\n\nclass XPathValidator\n{\n    public static bool ValidateXPath(string xpath, XmlSchema schema)\n    {\n        // Create a Saxon processor\n        Processor processor = new Processor(true);\n\n        // Create an XPath expression\n        XPathCompiler compiler = processor.NewXPathCompiler();\n        XPathExpression expr = compiler.Compile(xpath);\n\n        // Create a schema validator\n        SchemaValidator validator = processor.SchemaManager.NewSchemaValidator(schema);\n\n        // Validate the XPath expression\n        try\n        {\n            validator.Validate(expr);\n            return true;\n        }\n        catch (SchemaException)\n        {\n            return false;\n        }\n    }\n}