var validFontFamilies = new ObservableCollection<FontFamily>();\n\nforeach (var fontFamily in Fonts.SystemFontFamilies)\n{\n    try\n    {\n        fontFamily.GetTypefaces(); // This will throw a FileFormatException if the font is invalid\n        validFontFamilies.Add(fontFamily);\n    }\n    catch (FileFormatException)\n    {\n        // Ignore the invalid font\n    }\n}\n\nMe.fontComboFast.ItemsSource = validFontFamilies;