using System.Text.RegularExpressions;\n\npublic class BrowserIdentifier\n{\n    public static (string browser, string version) IdentifyBrowser(string userAgent)\n    {\n        var chromeRegex = new Regex(@"Chrome/(\d+\.\d+\.\d+\.\d+)");\n        var firefoxRegex = new Regex(@"Firefox/(\d+\.\d+)");\n        var edgeRegex = new Regex(@"Edg/(\d+\.\d+\.\d+\.\d+)");\n        var safariRegex = new Regex(@"Safari/(\d+\.\d+)");\n        var ieRegex = new Regex(@"MSIE (\d+\.\d+)");\n\n        if (chromeRegex.IsMatch(userAgent))\n        {\n            return ("Chrome", chromeRegex.Match(userAgent).Groups[1].Value);\n        }\n        else if (firefoxRegex.IsMatch(userAgent))\n        {\n            return ("Firefox", firefoxRegex.Match(userAgent).Groups[1].Value);\n        }\n        else if (edgeRegex.IsMatch(userAgent))\n        {\n            return ("Edge", edgeRegex.Match(userAgent).Groups[1].Value);\n        }\n        else if (safariRegex.IsMatch(userAgent))\n        {\n            return ("Safari", safariRegex.Match(userAgent).Groups[1].Value);\n        }\n        else if (ieRegex.IsMatch(userAgent))\n        {\n            return ("Internet Explorer", ieRegex.Match(userAgent).Groups[1].Value);\n        }\n        else\n        {\n            return ("Unknown", string.Empty);\n        }\n    }\n}