private WidgetFormViewModel PopulateWidgetFormViewModel(Widget widget)\n{\n    var employees = _widgetService.GetAllEmployees();\n    return new WidgetFormViewModel()\n    {\n        EmployeeList = new SelectList(employees, "ID", "Name", widget.Employee),\n        Widget = widget,\n        WidgetID = widget.ID\n    };\n}\n\n// GET: /Widget/Edit/1\npublic ActionResult Edit(int id)\n{\n    var widget = _widgetService.GetWidgetByID(id);\n    return View("Edit", PopulateWidgetFormViewModel(widget));\n}\n\n// POST: /Widget/Edit\npublic ActionResult Edit(WidgetFormViewModel viewModel)\n{\n    // ... (rest of your code)\n\n    if (!ModelState.IsValid)\n    {\n        var widget = _widgetService.GetWidgetByWidgetID(viewModel.WidgetID);\n        return View("Edit", PopulateWidgetFormViewModel(widget));\n    }\n    else\n    {\n        // Update was successful\n        return RedirectToAction("List");\n    }\n}