using System.IO;\nusing iTextSharp.text.pdf;\n\n// Create the PDF file on the server\nstring pdfFilePath = "path/to/pdf/file.pdf";\nusing (FileStream fs = new FileStream(pdfFilePath, FileMode.Create))\n{\n    // Generate the PDF using ITextSharp\n    Document doc = new Document();\n    PdfWriter writer = PdfWriter.GetInstance(doc, fs);\n    // ...\n}\n\n// Write the PDF file to the response stream\nResponse.Clear();\nResponse.ContentType = "application/pdf";\nResponse.AppendHeader("Content-Disposition", "attachment; filename=file.pdf");\nusing (FileStream fs = new FileStream(pdfFilePath, FileMode.Open))\n{\n    byte[] buffer = new byte[fs.Length];\n    fs.Read(buffer, 0, buffer.Length);\n    Response.OutputStream.Write(buffer, 0, buffer.Length);\n}\n\n// Delete the PDF file\nFile.Delete(pdfFilePath);