using System.Data.OleDb;\n\n// connect to the MS Access database\nstring connectionString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=C:\\Path\\To\\Your\\Database.mdb";\nOleDbConnection connection = new OleDbConnection(connectionString);\nconnection.Open();\n\n// create a command to update the table\nOleDbCommand command = new OleDbCommand("SELECT * FROM YourTable", connection);\n\n// create a data reader to iterate through the records\nOleDbDataReader reader = command.ExecuteReader();\n\nwhile (reader.Read())\n{\n    // perform your business logic here\n    decimal salary = reader["Salary"].ToDecimal() * factor;\n    int someNumber = GetMyBusinessRuleOn(reader["SomeNumber"].ToInt32());\n\n    // update the record\n    OleDbCommand updateCommand = new OleDbCommand("UPDATE YourTable SET Salary = ?, SomeNumber = ? WHERE ID = ?", connection);\n    updateCommand.Parameters.AddWithValue("@Salary", salary);\n    updateCommand.Parameters.AddWithValue("@SomeNumber", someNumber);\n    updateCommand.Parameters.AddWithValue("@ID", reader["ID"].ToInt32());\n    updateCommand.ExecuteNonQuery();\n}\n\n// close the reader and connection\nreader.Close();\nconnection.Close();