<asp:CustomValidator ID="cvAdvancedSearch" runat="server" \n    ErrorMessage="Please enter at least one search criteria" \n    OnServerValidate="cvAdvancedSearch_ServerValidate" />\n\n// Code-behind\nprotected void cvAdvancedSearch_ServerValidate(object source, ServerValidateEventArgs args)\n{\n    // Check if all fields are empty\n    if (string.IsNullOrWhiteSpace(txtField1.Text) && \n        string.IsNullOrWhiteSpace(txtField2.Text) && \n        string.IsNullOrWhiteSpace(txtField3.Text))\n    {\n        args.IsValid = false;\n    }\n    else\n    {\n        args.IsValid = true;\n    }\n}