using System;\nusing System.Security.Cryptography;\n\nclass SecureExample\n{\n    public static void Main(string[] args)\n    {\n        // Use a secure random number generator\n        var rng = new RNGCryptoServiceProvider();\n        var randomBytes = new byte[16];\n        rng.GetBytes(randomBytes);\n\n        // Use a secure hash function\n        var sha256 = SHA256.Create();\n        var input = "Hello, World!";\n        var hashedBytes = sha256.ComputeHash(input.GetBytes());\n\n        Console.WriteLine("Random bytes: " + BitConverter.ToString(randomBytes));\n        Console.WriteLine("Hashed input: " + BitConverter.ToString(hashedBytes));\n    }\n}