private static void SetAppPoolCredentials(string serverName, string appPoolName, string username, string password)\n{\n    string metabasePath = string.Format("IIS://{0}/W3SVC/AppPools/{1}", serverName, appPoolName);\n    try\n    {\n        DirectoryEntry appPool = new DirectoryEntry(metabasePath);\n        appPool.Properties["WAMUserName"].Value = username;\n        appPool.Properties["WAMUserPass"].Value = password;\n        appPool.CommitChanges();\n        Console.WriteLine("AppPool credentials updated.");\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine("Failed to set AppPool credentials with the following exception: \n{0}", ex.Message);\n    }\n}