using System;\nusing System.Security.AccessControl;\nusing System.ServiceProcess;\n\nclass Program\n{\n    static void Main()\n    {\n        // Get the W32tm service\n        ServiceController service = new ServiceController("W32Time");\n\n        // Set the security descriptor\n        SecurityDescriptor sd = new SecurityDescriptor();\n        sd.DiscretionaryAcl.AddAccess(AceFlags.AccessDenied, new SecurityIdentifier(WellKnownSidType.WorldSid, null), 0x0008);\n        sd.DiscretionaryAcl.AddAccess(AceFlags.AccessAllowed, new SecurityIdentifier(WellKnownSidType.LocalSystemSid, null), 0x0008);\n        sd.DiscretionaryAcl.AddAccess(AceFlags.AccessAllowed, new SecurityIdentifier(WellKnownSidType.BuiltinAdministratorsSid, null), 0x0008);\n        service.ServiceHandle.SetSecurityDescriptor(sd);\n    }\n}