using Microsoft.SharePoint.Client;\nusing System;\nusing System.Collections.Generic;\n\nnamespace SharePointGroupUsers\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // SharePoint site URL\n            string siteUrl = "https://your-sharepoint-site.sharepoint.com";\n\n            // Group name to retrieve users from\n            string groupName = "YourGroupName";\n\n            // Credentials to access SharePoint\n            string username = "your-sharepoint-username";\n            string password = "your-sharepoint-password";\n\n            // Create a new SharePoint context\n            ClientContext context = new ClientContext(siteUrl);\n\n            // Set the credentials for the context\n            context.Credentials = new SharePointOnlineCredentials(username, password);\n\n            // Ensure the group exists, and if so, get its users\n            GroupCollection groups = context.Web.SiteGroups;\n            context.Load(groups);\n            context.ExecuteQuery();\n\n            foreach (Group group in groups)\n            {\n                if (group.Title == groupName)\n                {\n                    UserCollection users = group.Users;\n                    context.Load(users);\n                    context.ExecuteQuery();\n\n                    foreach (User user in users)\n                    {\n                        Console.WriteLine(user.LoginName);\n                    }\n                }\n            }\n\n            Console.ReadLine();\n        }\n    }\n}