using System.Drawing;\n\npublic static Color Tint(Color color, float percent)\n{\n    if (percent < 0 || percent > 100)\n    {\n        throw new ArgumentOutOfRangeException("percent", "Percent must be between 0 and 100");\n    }\n\n    float factor = percent / 100;\n    byte r = (byte)(color.R + (255 - color.R) * (1 - factor));\n    byte g = (byte)(color.G + (255 - color.G) * (1 - factor));\n    byte b = (byte)(color.B + (255 - color.B) * (1 - factor));\n\n    return Color.FromArgb(r, g, b);\n}\n\npublic static string ToHex(Color color)\n{\n    return $"#{color.R:X2}{color.G:X2}{color.B:X2}";\n}\n\n// Example usage:\nColor originalColor = Color.FromArgb(128, 0, 0); // A dark red color\nColor tintedColor = Tint(originalColor, 90); // 90% tint of the original color\nstring hexColor = ToHex(tintedColor); // Convert to hex HTML color value