using System;\nusing System.IO;\nusing System.Web;\n\npublic class ImageUploadHandler : IHttpHandler\n{\n  public void ProcessRequest(HttpContext context)\n  {\n    // Get the uploaded file\n    HttpPostedFile file = context.Request.Files["image"];\n    // Save the file to the desired folder\n    string folderPath = Server.MapPath("~/images/");\n    string fileName = Path.GetFileName(file.FileName);\n    file.SaveAs(folderPath + fileName);\n    // Return the file name as JSON\n    context.Response.ContentType = "application/json";\n    context.Response.Write("{\"fileName\":\"" + fileName + "\"}");\n  }\n\n  public bool IsReusable\n  {\n    get { return true; }\n  }\n}