// Get the editor content\nstring content = CKEDITOR.instances.editor_id.getData();\n\n// Encode the content\nstring encodedContent = HttpUtility.HtmlEncode(content);\n\n// Create a parameterized query\nSqlCommand cmd = new SqlCommand("INSERT INTO table_name (content) VALUES (@content)", dbConnection);\ncmd.Parameters.AddWithValue("@content", encodedContent);\ncmd.ExecuteNonQuery();