using System.IdentityModel.Tokens.Jwt;\nusing System.Security.Claims;\nusing Microsoft.IdentityModel.Tokens;\n\n// Client requests an access token\nstring clientId = "your_client_id";\nstring clientSecret = "your_client_secret";\nstring tokenEndpoint = "https://your-web-service.com/token";\n\n// Validate client ID and secret\nif (ValidateClientIdAndSecret(clientId, clientSecret))\n{\n    // Generate an access token\n    var claims = new[]\n    {\n        new Claim(ClaimTypes.Name, clientId),\n        new Claim(ClaimTypes.Role, "client")\n    };\n\n    var token = new JwtSecurityToken(\n        issuer: "your-web-service.com",\n        audience: "your-web-service.com",\n        claims: claims,\n        expires: DateTime.UtcNow.AddMinutes(30),\n        signingCredentials: new SigningCredentials(new SymmetricSecurityKey(yourSecretKey), SecurityAlgorithms.HmacSha256)\n    );\n\n    var tokenString = new JwtSecurityTokenHandler().WriteToken(token);\n\n    // Return the access token\n    return tokenString;\n}