// assume 'dataView' is the DataView on top of your DataSet\n// assume 'dbConnection' is an open connection to your database\n\n// retrieve the updated data from the database\nDataTable dbData = new DataTable();\nusing (SqlCommand cmd = new SqlCommand("SELECT * FROM YourTable", dbConnection)) {\n    using (SqlDataAdapter adapter = new SqlDataAdapter(cmd)) {\n        adapter.Fill(dbData);\n    }\n}\n\n// custom merge algorithm\nforeach (DataRowView dataRowView in dataView) {\n    DataRow dbRow = dbData.Rows.Find(dataRowView["ID"]);\n    if (dbRow != null) {\n        // update the DataSet row with the changes from the database\n        foreach (DataColumn column in dataRowView.Row.Table.Columns) {\n            if (column.ColumnName != "ID") {\n                dataRowView[column.ColumnName] = dbRow[column.ColumnName];\n            }\n        }\n    }\n}