using System;\n\nclass Program\n{\n    static void Main()\n    {\n        string csvString = "Name,Age,Country\nJohn,25,USA\nAlice,30,UK";\n\n        string[] rows = csvString.Split('\n');\n\n        foreach (string row in rows)\n        {\n            string[] columns = row.Split(',');\n\n            foreach (string column in columns)\n            {\n                Console.Write(column + " | ");\n            }\n            Console.WriteLine();\n        }\n    }\n}