// Define a DbContext that uses a SQL Server connection\npublic class MyDbContext : DbContext\n{\n    public MyDbContext() : base("name=SqlServerConnection") { }\n    public DbSet<MyEntity> MyEntities { get; set; }\n}\n\n// Define a DbContext that uses a Visual FoxPro connection\npublic class MyFoxProDbContext : DbContext\n{\n    public MyFoxProDbContext() : base("name=FoxProConnection") { }\n    public DbSet<MyEntity> MyEntities { get; set; }\n}\n\n// Use the DbContext in your Data Access Layer\npublic class MyDataService\n{\n    private readonly DbContext _context;\n\n    public MyDataService(DbContext context)\n    {\n        _context = context;\n    }\n\n    public List<MyEntity> GetEntities()\n    {\n        return _context.MyEntities.ToList();\n    }\n}