using Oracle.ManagedDataAccess.Client;\n\n// Create a connection and command\nOracleConnection conn = new OracleConnection("connection string");\nOracleCommand cmd = new OracleCommand("INSERT INTO table_name (column1, column2) VALUES (:param1, :param2) RETURNING id INTO :id", conn);\n\n// Add parameters\ncmd.Parameters.Add(new OracleParameter("param1", "value1"));\ncmd.Parameters.Add(new OracleParameter("param2", "value2"));\ncmd.Parameters.Add(new OracleParameter("id", OracleDbType.Int32, ParameterDirection.Output));\n\n// Execute the command\nconn.Open();\ncmd.ExecuteNonQuery();\n\n// Retrieve the PK value\nint newId = (int)cmd.Parameters["id"].Value;\n\nconn.Close();