using NAudio.Wave;\n\nclass Program\n{\n    static void Main()\n    {\n        // Set up the wave format\n        WaveFormat waveFormat = new WaveFormat(44100, 1);\n\n        // Create a wave provider\n        IWaveProvider waveProvider = new BufferedWaveProvider(waveFormat);\n\n        // Create a wave out\n        using (WaveOut waveOut = new WaveOut(WaveCallbackInfo.FunctionCallback()))\n        {\n            waveOut.Init(waveProvider);\n\n            // Generate waveform data\n            float[] samples = new float[44100];\n            for (int i = 0; i < samples.Length; i++)\n            {\n                samples[i] = (float)Math.Sin(2 * Math.PI * 440 * i / 44100); // 440 Hz\n            }\n\n            // Add the waveform data to the wave provider\n            waveProvider.AddSamples(samples, 0, samples.Length);\n\n            // Play the sound\n            waveOut.Play();\n\n            // Wait for the sound to finish playing\n            while (waveOut.PlaybackState == PlaybackState.Playing)\n            {\n                Thread.Sleep(100);\n            }\n        }\n    }\n}